import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { ChevronLeft, ChevronRight, Star, Clock, Heart, ShoppingCart, AlertCircle } from 'lucide-react';
import { Alert, AlertDescription } from '../components/ui/Alert';

const Trending = () => {
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [index, setIndex] = useState(0);
  const [wishlist, setWishlist] = useState(new Set());
  const [cart, setCart] = useState(new Set());
  const [notification, setNotification] = useState({ show: false, message: '', type: '' });


  // Future API integration
  // useEffect(() => {
  //   const fetchBooks = async () => {
  //     try {
  //       setLoading(true);
  //       const response = await axios.get(`${process.env.REACT_APP_API_URL}/api/books/trending`);
  //       setBooks(response.data);
  //     } catch (err) {
  //       setError('Failed to fetch books. Please try again later.');
  //     } finally {
  //       setLoading(false);
  //     }
  //   };
  //     fetchBooks();
  //   }, []);

  // Fetch books from API
  useEffect(() => {
    const fetchBooks = async () => {
      try {
        setLoading(true);
        // Hardcoded book data (temporary replacement for API call)
        const booksData = [
          {
            id: 1,
            title: "The Midnight Library",
            author: "Matt Haig",
            description: "Between life and death there is a library, and within that library, the shelves go on forever. Every book provides a chance to try another life you could have lived.",
            price: 24.99,
            imageUrl: "data:image/jpeg;base64,",
            rating: 4.8,
            reviewCount: 3547,
            isLimitedOffer: true,
            discount: 15,
            category: "Fiction",
            tags: ["Bestseller", "Fantasy", "Contemporary"]
          },
          {
            id: 2,
            title: "Atomic Habits",
            author: "James Clear",
            description: "A revolutionary guide to using tiny changes in behavior to transform your life. Learn how to build good habits and break bad ones with proven strategies.",
            price: 29.99,
            imageUrl: "data:image/jpeg;base64,",
            rating: 4.9,
            reviewCount: 5280,
            isLimitedOffer: true,
            discount: 20,
            category: "Self-Help",
            tags: ["Bestseller", "Productivity", "Psychology"]
          },
          {
            id: 3,
            title: "Project Hail Mary",
            author: "Andy Weir",
            description: "A lone astronaut must save humanity from a catastrophic extinction event. From the author of The Martian comes a thrilling new adventure in space.",
            price: 27.99,
            imageUrl: "data:image/jpeg;base64,",
            rating: 4.7,
            reviewCount: 2890,
            isLimitedOffer: false,
            category: "Science Fiction",
            tags: ["Sci-Fi", "Adventure", "Space"]
          },
          {
            id: 4,
            title: "Tomorrow, and Tomorrow, and Tomorrow",
            author: "Gabrielle Zevin",
            description: "A modern tale about the friendship of two creative partners spanning decades, success, failure, and games that define their lives.",
            price: 26.99,
            imageUrl: "data:image/jpeg;base64,",
            rating: 4.6,
            reviewCount: 1876,
            isLimitedOffer: true,
            discount: 10,
            category: "Literary Fiction",
            tags: ["Award Winner", "Contemporary", "Gaming"]
          },
          {
            id: 5,
            title: "The Psychology of Money",
            author: "Morgan Housel",
            description: "Timeless lessons on wealth, greed, and happiness. Exploring the strange ways people think about money and teaching you how to make better sense of it.",
            price: 22.99,
            imageUrl: "data:image/jpeg;base64,",
            rating: 4.8,
            reviewCount: 4123,
            isLimitedOffer: false,
            category: "Finance",
            tags: ["Business", "Personal Finance", "Bestseller"]
          }
        ];
        setBooks(booksData);
      } catch (err) {
        setError('Failed to fetch books. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    fetchBooks();
  }, []);

  // Load wishlist and cart from localStorage on component mount
  useEffect(() => {
    const savedWishlist = JSON.parse(localStorage.getItem('wishlist')) || [];
    const savedCart = JSON.parse(localStorage.getItem('cart')) || [];
    setWishlist(new Set(savedWishlist));
    setCart(new Set(savedCart));
  }, []);

  // Auto-scroll functionality
  useEffect(() => {
    if (books.length === 0) return;
    const interval = setInterval(() => {
      setIndex((prevIndex) => (prevIndex + 1) % books.length);
    }, 6000);
    return () => clearInterval(interval);
  }, [books.length]);

  // Show notification
  const showNotification = (message, type = 'success') => {
    setNotification({ show: true, message, type });
    setTimeout(() => setNotification({ show: false, message: '', type: '' }), 3000);
  };

  // Handle wishlist toggle
  const toggleWishlist = async (bookId) => {
    try {
      if (wishlist.has(bookId)) {
        await axios.delete(`${process.env.REACT_APP_API_URL}/api/wishlist/${bookId}`);
        wishlist.delete(bookId);
      } else {
        await axios.post(`${process.env.REACT_APP_API_URL}/api/wishlist`, { bookId });
        wishlist.add(bookId);
      }
      
      const newWishlist = new Set(wishlist);
      setWishlist(newWishlist);
      localStorage.setItem('wishlist', JSON.stringify([...newWishlist]));
      
      showNotification(
        wishlist.has(bookId) 
          ? 'Added to wishlist successfully!' 
          : 'Removed from wishlist successfully!'
      );
    } catch (error) {
      showNotification('Failed to update wishlist. Please try again.', 'error');
    }
  };

  // Handle add to cart
  const addToCart = async (bookId) => {
    try {
      await axios.post(`${process.env.REACT_APP_API_URL}/api/cart`, { bookId });
      const newCart = new Set(cart).add(bookId);
      setCart(newCart);
      localStorage.setItem('cart', JSON.stringify([...newCart]));
      showNotification('Added to cart successfully!');
    } catch (error) {
      showNotification('Failed to add to cart. Please try again.', 'error');
    }
  };

  // Handle buy now
  const handleBuyNow = async (bookId) => {
    try {
      const response = await axios.post(`${process.env.REACT_APP_API_URL}/api/checkout`, {
        items: [{ bookId, quantity: 1 }]
      });
      window.location.href = response.data.checkoutUrl;
    } catch (error) {
      showNotification('Failed to initiate checkout. Please try again.', 'error');
    }
  };

  const navigate = (direction) => {
    setIndex((prevIndex) => {
      if (direction === 'next') {
        return (prevIndex + 1) % books.length;
      }
      return (prevIndex - 1 + books.length) % books.length;
    });
  };

  if (loading) {
    return (
      <div className="w-full bg-gradient-to-b from-slate-900 to-slate-800 py-20">
        <div className="container mx-auto px-4">
          <div className="h-8 w-64 mx-auto mb-12 bg-slate-700 animate-pulse rounded" />
          <div className="flex justify-center">
            <div className="h-96 w-full max-w-2xl bg-slate-700 animate-pulse rounded-xl" />
          </div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="w-full bg-gradient-to-b from-slate-900 to-slate-800 py-20">
        <div className="container mx-auto px-4 text-center">
          <Alert variant="destructive">
            <AlertCircle className="h-4 w-4" />
            <AlertDescription>{error}</AlertDescription>
          </Alert>
        </div>
      </div>
    );
  }

  return (
    <div className="w-full bg-gradient-to-b from-slate-900 to-slate-800 py-12 sm:py-20">
      <div className="container mx-auto px-3 sm:px-4">
        {notification.show && (
          <div className="fixed top-4 right-4 z-50 w-[calc(100%-2rem)] sm:w-auto">
            <Alert variant={notification.type === 'error' ? 'destructive' : 'default'}>
              <AlertDescription className="text-sm sm:text-base">{notification.message}</AlertDescription>
            </Alert>
          </div>
        )}

        <div className="flex flex-col items-center mb-8 sm:mb-12">
          <h2 className="text-2xl sm:text-3xl md:text-4xl lg:text-5xl font-bold text-white text-center mb-3 sm:mb-4">
            Trending Books
          </h2>
          <div className="h-1 w-20 sm:w-24 bg-gradient-to-r from-purple-400 to-pink-400 rounded-full" />
        </div>

        <div className="relative max-w-6xl mx-auto">
          <div className="flex justify-between absolute top-1/2 -translate-y-1/2 right-4 sm:right-8 w-full -mx-3 sm:-mx-6 md:-mx-8 z-1">
            <button
              onClick={() => navigate('prev')}
              className="bg-white/10 hover:bg-white/20 text-white p-2 sm:p-3 md:p-4 rounded-full backdrop-blur-sm transition-all duration-300 hover:scale-110"
              aria-label="Previous book"
            >
              <ChevronLeft className="w-5 h-5 sm:w-6 sm:h-6" />
            </button>
            <button
              onClick={() => navigate('next')}
              className="bg-white/10 hover:bg-white/20 text-white p-2 sm:p-3 md:p-4 rounded-full backdrop-blur-sm transition-all duration-300 hover:scale-110"
              aria-label="Next book"
            >
              <ChevronRight className="w-5 h-5 sm:w-6 sm:h-6" />
            </button>
          </div>

          <div className="overflow-hidden mx-auto rounded-xl">
            <div
              className="flex transition-transform duration-500 ease-out"
              style={{ transform: `translateX(-${index * 100}%)` }}
            >
              {books.map((book) => (
                <div key={book.id} className="min-w-full px-2 sm:px-4">
                  <div className="bg-gradient-to-br from-slate-800/80 to-slate-900/80 border border-slate-700/50 rounded-xl backdrop-blur-sm shadow-xl">
                    <div className="p-4 sm:p-6 md:p-8">
                      <div className="flex flex-col md:flex-row gap-4 sm:gap-6 md:gap-8 items-center">
                        <div className="w-40 sm:w-48 h-52 sm:h-64 relative flex-shrink-0 group">
                          <img
                            src={book.imageUrl || "/api/placeholder/192/256"}
                            alt={book.title}
                            className="w-full h-full object-cover rounded-lg shadow-xl transition-transform duration-300 group-hover:scale-105"
                          />
                          <button
                            onClick={() => toggleWishlist(book.id)}
                            className="absolute top-2 right-2 p-1.5 sm:p-2 bg-white/10 hover:bg-white/20 rounded-full backdrop-blur-sm transition-all"
                          >
                            <Heart
                              className={`w-4 h-4 sm:w-5 sm:h-5 ${wishlist.has(book.id) ? 'fill-pink-400 text-pink-400' : 'text-white'}`} 
                            />
                          </button>
                        </div>
                        <div className="flex-1 text-center md:text-left">
                          <h3 className="text-xl sm:text-2xl md:text-3xl font-bold text-white mb-2">
                            {book.title}
                          </h3>
                          <p className="text-purple-300 mb-3 sm:mb-4 text-base sm:text-lg">by {book.author}</p>
                          <p className="text-slate-300 mb-4 sm:mb-6 line-clamp-3 text-sm sm:text-base md:text-lg">
                            {book.description}
                          </p>
                          <div className="flex flex-wrap gap-3 sm:gap-4 justify-center md:justify-start items-center mb-4 sm:mb-6">
                            <div className="flex items-center gap-2">
                              <Star className="w-4 h-4 sm:w-5 sm:h-5 text-yellow-400 fill-yellow-400" />
                              <span className="text-white font-medium text-sm sm:text-base">{book.rating}</span>
                              <span className="text-slate-400 text-sm sm:text-base">({book.reviewCount.toLocaleString()} reviews)</span>
                            </div>
                            {book.isLimitedOffer && (
                              <div className="flex items-center gap-2">
                                <Clock className="w-4 h-4 sm:w-5 sm:h-5 text-purple-400" />
                                <span className="text-slate-400 text-sm sm:text-base">Limited time offer</span>
                              </div>
                            )}
                          </div>
                          <div className="flex flex-wrap gap-3 sm:gap-4 justify-center md:justify-start">
                            <button
                              onClick={() => handleBuyNow(book.id)}
                              className="px-4 sm:px-6 py-2 sm:py-3 bg-gradient-to-r from-purple-500 to-pink-500 text-white rounded-full font-medium hover:opacity-90 transition-opacity flex items-center gap-2 text-sm sm:text-base"
                            >
                              Buy Now - ${book.price}
                            </button>
                            <button
                              onClick={() => addToCart(book.id)}
                              disabled={cart.has(book.id)}
                              className="px-4 sm:px-6 py-2 sm:py-3 bg-white/10 text-white rounded-full font-medium hover:bg-white/20 transition-colors flex items-center gap-2 disabled:opacity-50 disabled:cursor-not-allowed text-sm sm:text-base"
                            >
                              <ShoppingCart className="w-4 h-4 sm:w-5 sm:h-5" />
                              {cart.has(book.id) ? 'In Cart' : 'Add to Cart'}
                            </button>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>

          <div className="flex justify-center mt-6 sm:mt-8 gap-2 sm:gap-3">
            {books.map((_, i) => (
              <button
                key={i}
                onClick={() => setIndex(i)}
                className={`h-1.5 sm:h-2 rounded-full transition-all duration-300 ${
                  i === index ? 'w-6 sm:w-8 bg-gradient-to-r from-purple-400 to-pink-400' : 'w-1.5 sm:w-2 bg-white/30'
                }`}
                aria-label={`Go to slide ${i + 1}`}
              />
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Trending;